import 'package:flutter/material.dart';import 'package:reglfit/constant/constants.dart';import 'package:reglfit/widgets/custom_image.dart';import 'package:reglfit/widgets/custom_text.dart';class CustomElevatedButton extends StatelessWidget {  CustomElevatedButton({    Key? key,    required this.text,    this.onTap,    this.backgroundColor,    this.textColor,    this.width = 350,    this.height = 15,    this.shape,    this.yaziIcPadding,    this.fontSize,    this.fontWeight,    this.icon,    this.iconSize,    this.iconColor,    this.ImageUrl,    this.ImageHeight = 30,    this.ImageWidth = 30,    this.ImageAlignment,    this.ImageColor,    this.Imagefit,  }) : super(key: key);  final String text;  final void Function()? onTap;  final Color? backgroundColor;  final Color? textColor;  final double width;  final double height;  final double? fontSize;  final FontWeight? fontWeight;  final IconData? icon;  final double? iconSize;  final Color? iconColor;  final String? ImageUrl;  final BoxFit? Imagefit;  final double ImageWidth;  final double ImageHeight;  final AlignmentGeometry? ImageAlignment;  final Color? ImageColor;  final OutlinedBorder? shape;  final EdgeInsetsGeometry? yaziIcPadding;  @override  Widget build(BuildContext context) {    return ElevatedButton(        onPressed: onTap,        style: ElevatedButton.styleFrom(          padding: yaziIcPadding,          backgroundColor: backgroundColor,          foregroundColor: textColor,          fixedSize: Size(width, height),          shape: shape ?? RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),        ),        child: Row(          mainAxisSize: MainAxisSize.min, // Adjusts size to the content          mainAxisAlignment: MainAxisAlignment.center,          crossAxisAlignment: CrossAxisAlignment.center,          children: [            if (ImageUrl != null && ImageUrl!.isNotEmpty)              Padding(                padding: const EdgeInsets.only(right: 10.0),                child: Image.asset(                  ImageUrl!,                  fit: BoxFit.fill,                  width: ImageWidth, // Resim genişliği                  height: ImageHeight,                ),              ), // Örnek olarak ağ üzerinden resim yükleme            if (icon != null) ...[              Padding(                padding: const EdgeInsets.only(right: 10.0),                child: Icon(                  icon,                  size: iconSize,                  color: iconColor,                ),              ),            ],            CustomText(              text: text,              fontSize: fontSize,              fontWeight: fontWeight,            ),          ],        ));  }}